package br.com.ifet.classes;

public class Modulo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 6;
		int y = 3;
		
		System.out.println(x+y);
		System.out.println(x-y);
		System.out.println(x/y);
		System.out.println(x*y);
		
		System.out.println(x%y);
		x = x + 1;
		System.out.println(x%y);

	}

}
